import { Component } from '@angular/core';
import { FormControl, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { FormBuilder, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-uploads',
  templateUrl: './uploads.component.html',
  styleUrls: ['./uploads.component.css']
})
export class UploadsComponent {
  uploadForm!: FormGroup;
  fileTypeError: boolean = false;
  constructor() { 
    this.uploadForm = this.formBuilder.group({
      document: [null]  // No validators, since file is not required.
    });
  }

  // uploadForm = new FormGroup({
  //   file: new FormControl<File | null>(null, [Validators.required]) 
  // });

  onFileSelected(event: Event): void {
    const element = event.target as HTMLInputElement;
    let fileList: FileList | null = element.files;
    if (fileList && fileList.length > 0) {
      const file = fileList[0];

      const fileControl = this.uploadForm.get('file');

      if (fileControl) {
        if (!file.name.endsWith('.xlsx')) {
          fileControl.setErrors({ incorrectFileType: true });
        } else {
          fileControl.setValue(file);
          fileControl.setErrors(null);
        }
      }
    }
  }


  runUpload() {
    if (this.uploadForm.valid) {
      const fileToUpload = this.uploadForm.value.file;
    }
  }

  hasError(controlName: string, errorName: string) {
    const control = this.uploadForm.get(controlName);
    return control && control.hasError(errorName);
  }

  onFileSelect(event: Event) {
    const element = event.currentTarget as HTMLInputElement;
    let file: File | null = element.files?.item(0) ?? null;

    // Reset previous errors
    this.fileTypeError = false;

    if (file) {
      // Check for CSV or XLS/XLSX file types
      if (/(.csv|.xls|.xlsx)$/i.test(file.name)) {
        this.uploadForm.patchValue({
          document: file
        });
      } else {
        this.fileTypeError = true;
        this.uploadForm.reset();
      }
    }
  }

  onUpload() {
    // Handle the form submission and file upload logic here.
  }

  resetUpload() {
    this.uploadForm.reset();
    this.fileTypeError = false;
  }
}
